import React from "react";

const Switch = (props) => {
  const { options, selectedIndex, onClick } = props;

  return (
    <div className="h-10 rounded overflow-hidden">
      <div className="relative flex h-full">
        {options.map((option, index) => (
          <div
            key={index}
            className={`flex-1 flex items-center justify-center text-black
              ${index === selectedIndex ? "bg-blue-500 text-white-900" : "bg-gray-200 text-gray-800"}`}
            onClick={() => onClick(index)}
          >
            <span className="text-xs capitalize">{option}</span>
          </div>
        ))}
        <div
          className="absolute top-0 h-full bg-blue-900"
          style={{
            width: `${100 / options.length}%`,
            left: `${(100 / options.length) * selectedIndex}%`,
          }}
        ></div>
      </div>
    </div>
  );
};

export default Switch;
